import './App.css';
import MintExampleABI from './MintExampleAbi.json';
import { ethers, BigNumber } from "ethers";
import { useEffect, useState } from 'react';


const mintExampleAddress = "0x5FbDB2315678afecb367f032d93F642f64180aa3"

function App() {
  //Connection
  const [accounts, setAccounts] = useState([]);

  async function connectAccounts(){
    if (window.ethereum) {
      const accounts = await window.ethereum.request({
        method: "eth_requestAccounts" 
      });
      setAccounts(accounts);
    }
  }

  useEffect(() => {
    connectAccounts();
  }, []);

  //MINTING
  const [mintAmount, setMintAmount] = useState(1);

  async function handleMint() {
    if (window.ethereum) {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const contract = new ethers.Contract(
        mintExampleAddress,
        MintExampleABI.abi,
        signer
      );
      try {
        const response = await contract.mint(BigNumber.from(mintAmount))
        console.log("response: ", response);
      } catch(err) {
        console.log("error: ", err);
      }
    } 
  }

  return (
    <div className="App">
      <div class="mint">
        <div class="nav">
          <ul class="menu">
            <li class="item"><a href="#about">About</a></li>
            <li class="item"><a href="#roadmap">Roadmap</a></li>
            <li class="item"><a href="#drip">Drip</a></li>
            <li class="item"><a href="#team">Team</a></li>
            <li class="item"><a href="#faq">FAQ</a></li>
            <li class="toggle"><a href="#"><i class="fas fa-bars"></i></a></li>
          </ul>
        </div>
        <div>
          This is how you create a mint button
          {accounts.length && (
            <div>
              <button onClick={() => setMintAmount(mintAmount - 1)}>-</button>
              {mintAmount}
              <button onClick={() => setMintAmount(mintAmount + 1)}>+</button>
              <button onClick={() => handleMint}>Mint</button>

            </div>
          )}
        </div>
      </div>
      <div id="about" class="about">

      </div>
      <div id="roadmap" class="roadmap">

      </div>
      <div id="team" class="team">

      </div>
      <div id="faq" class="faq">

      </div>
    </div>
  );
}

export default App;
